import java.io.*;import java.util.*;class Solution{public static void main(String[]a)throws Exception{var s=new StringTokenizer(new String(System.in.readAllBytes()));int n=s.hasMoreTokens()?Integer.parseInt(s.nextToken()):0,A=0,B=1_000_000_000;for(int i=0;i<n&&s.hasMoreTokens();i++){int t=Integer.parseInt(s.nextToken()),x=Math.abs(t);if(x<B||x==B&&t>A){B=x;A=t;}}System.out.print(B<1_000_000_000?A:0);}}