use std::io::*;fn main(){let mut s=String::new();stdin().read_to_string(&mut s).unwrap();let mut it=s.split_whitespace();let n:usize=it.next().unwrap_or("0").parse().unwrap_or(0);let(mut a,mut b)=(0,1_000_000_000i32);for _ in 0..n{if let Some(x)=it.next(){let t=i32::from_str_radix(x,10).unwrap_or(0);let u=t.abs();if u<b||u==b&&t>a{b=u;a=t}}}print!("{}",if b<1_000_000_000{a}else{0});}